const initState = {
    authError: null
}

const authReducer = (state = initState, action) => {
    switch(action.type) {
        case 'LOGIN_ERROR':
            return {
                ...state,
                authError: 'Erro no Login'
            }
        case 'LOGIN_SUCCESS':
            console.log('SUCCESS LOGIN')
            return {
                ...state,
                authError: null
            }
        case 'SIGNOUT_SUCCESS':
            console.log('SIGNOUT_SUCCESS')
            return state;
        default:
            return state;

    }
};

export default authReducer;
